-- Write your PostgreSQL query statement below
SELECT ROUND(((SUM(CASE WHEN d.order_date = d.customer_pref_delivery_date THEN 1 ELSE 0 END)::NUMERIC/(SELECT COUNT(DISTINCT customer_id) FROM Delivery)::NUMERIC))*100, 2) AS immediate_percentage FROM Delivery AS d INNER JOIN (SELECT customer_id, MIN(order_date) first_date FROM Delivery GROUP BY customer_id) AS f ON d.customer_id = f.customer_id AND d.order_date = f.first_date;

-- Write your MySQL query statement below
SELECT ROUND(((SUM(CASE WHEN d.order_date = d.customer_pref_delivery_date THEN 1 ELSE 0 END)/(SELECT COUNT(DISTINCT customer_id) FROM Delivery)))*100, 2) AS immediate_percentage FROM Delivery AS d INNER JOIN (SELECT customer_id, MIN(order_date) first_date FROM Delivery GROUP BY customer_id) AS f ON d.customer_id = f.customer_id AND d.order_date = f.first_date;