SELECT d.name AS Department, e.name AS Employee, e.salary AS salary FROM Employee AS e INNER JOIN (SELECT d.id AS depId, (SELECT e.salary AS salary FROM Employee AS e WHERE e.departmentId = d.id GROUP BY salary ORDER BY e.salary DESC LIMIT 1) AS top1, (SELECT e.salary AS salary FROM Employee AS e WHERE e.departmentId = d.id GROUP BY salary ORDER BY e.salary DESC LIMIT 1 OFFSET 1) AS top2, (SELECT e.salary AS salary FROM Employee AS e WHERE e.departmentId = d.id GROUP BY salary ORDER BY e.salary DESC LIMIT 1 OFFSET 2) AS top3 FROM Department AS d) AS t ON (e.salary = t.top1 OR e.salary = t.top2 OR e.salary = t.top3) AND t.depId = e.departmentId INNER JOIN Department AS d ON d.id = e.departmentId;