SELECT lb.book_id AS book_id, lb.title AS title, lb.author AS author, lb.genre AS genre, lb.publication_year AS publication_year, total_copies AS current_borrowers FROM library_books AS lb INNER JOIN (SELECT book_id, COUNT(*) AS count FROM borrowing_records WHERE return_date IS NULL GROUP BY book_id) AS br ON lb.book_id = br.book_id AND lb.total_copies = br.count ORDER BY current_borrowers DESC, lb.title ASC;